    private void btnAtrasActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAtrasActionPerformed
        this.dispose();
    }//GEN-LAST:event_btnAtrasActionPerformed
DesaArchivos desarchi = new DesaArchivos();
    private void btnAceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAceptarActionPerformed
        String Jugador1 = txtJugador1.getText();
        String Jugador2 = txtJugador2.getText();
        if (!Jugador1.equals("") & !Jugador2.equals("")) {
            if (!Jugador1.equals(Jugador2)) {
                frmJuegoQuarto ventanaMatriz = new frmJuegoQuarto();
                ventanaMatriz.pack();
                ventanaMatriz.setVisible(true);
                ventanaMatriz.lblNombre1.setText(txtJugador1.getText());
                ventanaMatriz.lblNombre2.setText(txtJugador2.getText());
                ventanaMatriz.lblTurno.setText(Jugador1);
                int npart = objNumPartida.partidas.size();
                if(npart==1){
                    objReporteJuga.reportes.add(new objReporteJuga(Jugador1,0,0,0,0,npart));
                    objReporteJuga.reportes.add(new objReporteJuga(Jugador2,0,0,0,0,npart));
                    desarchi.guardarJugador();
                }
                this.dispose();
            } else {
                JOptionPane.showMessageDialog(null, "Nombres iguales", "ERROR", JOptionPane.ERROR_MESSAGE);
            }
        } else {
            JOptionPane.showMessageDialog(null, "Campo vac√≠o", "ERROR", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnAceptarActionPerformed

